import './App.css';
import React, { Component } from 'react';
import apiKey from './config';
import axios from 'axios';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Header from './Components/Header';
import Nav from './Components/Nav';
import SearchBar from './Components/SearchBar';
import SearchResults from './Components/SearchResults';

const api = apiKey;


class App extends Component {

  constructor() {
    super();
    this.state = {
      pics: [],
      loading: true
    };
  } 

  componentDidMount() {
    this.mainSearch();
    this.beerSearch();
    this.wineSearch();
    this.vodkaSearch();
  }

  mainSearch = ( query = 'guitars') => {
    axios.get(`https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=${api}&tags=${query}&sort=relevance&per_page=24&format=json&nojsoncallback=1`)
      .then(response => 
        {
        this.setState({
          mainPics: response.data.photos.photo,
          loading: false
        });
      })
      .catch(function (error) {
        console.log('Error fetching and parsing data', error);
      })
  }

  beerSearch = (query = 'kieselguitars' ) => {
    axios.get(`https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=${api}&tags=${query}&sort=relevance&per_page=24&format=json&nojsoncallback=1`)
      .then(response => 
        {
        this.setState({
          beerPics: response.data.photos.photo,
          loading: false
        });
      })
      .catch(function (error) {
        console.log('Error fetching and parsing data', error);
      })
  }

  wineSearch = (query = 'fenderguitars' ) => {
    axios.get(`https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=${api}&tags=${query}&sort=relevance&per_page=24&format=json&nojsoncallback=1`)
      .then(response => 
        {
        this.setState({
          winePics: response.data.photos.photo,
          loading: false
        });
      })
      .catch(function (error) {
        console.log('Error fetching and parsing data', error);
      })
  }

  vodkaSearch = (query = 'gibsonguitars' ) => {
    axios.get(`https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=${api}&tags=${query}&sort=relevance&per_page=24&format=json&nojsoncallback=1`)
      .then(response => 
        {
        this.setState({
          vodkaPics: response.data.photos.photo,
          loading: false
        });
      })
      .catch(function (error) {
        console.log('Error fetching and parsing data', error);
      })
  }

  render() {
    return (
      <BrowserRouter>
        <div className="container">
          <Header />
          <SearchBar searchPhoto={this.mainSearch}/>
          <Nav />
          <Switch>
            <Route exact path = '/' 
              render = { 
                () => (this.state.loading) 
                  ? <p> Loading...</p> 
                  : <SearchResults data={this.state.mainPics} />} />
            <Route exact path = '/kieselguitars'
              render = {
                () => (this.state.loading) 
                  ? <p> Loading...</p> 
                  : <SearchResults data={this.state.beerPics} />} /> 
              <Route exact path = '/fenderguitars'
                render = {
                  () => (this.state.loading) 
                    ? <p> Loading...</p> 
                    : <SearchResults data={this.state.winePics} />} />
              <Route exact path = '/gibsonguitars'
                render = {
                  () => (this.state.loading) 
                    ? <p> Loading...</p> 
                    : <SearchResults data={this.state.vodkaPics} />} />
              <Route path = '/search'
                render = {
                  () => (this.state.loading) 
                    ? <p> Loading...</p> 
                    : <SearchResults data={this.state.mainPics} />} />
            </Switch>
        </div> 
      </BrowserRouter>
    );
  }
}

export default App;
